For Loop for Placebo Dataset

for (i in 1:10) {
  if(my_list[[2]]$post_bp[i] &gt; 130) {
    my_list[[2]]$status[i] &lt;- "high"
}   else if(my_list[[2]]$post_bp[i] &gt; 120) {
      my_list[[2]]$status[i] &lt;- "borderline"
}   else {
      my_list[[2]]$status[i] &lt;- "optimal"
}
}
